import React, { useState, useEffect } from "react";
import Newslist from "./newslist";

export default function Main() {
  const [news, setNews] = useState([]);
  const [pageno, setPageno] = useState(1);
  function handleClickOnPrevious() {
    setPageno(e => e + 1)
  }

  function handleClickOnNext() {
    
  }
  
    const url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=246319e5bf884ea5814b2a0b9f940a09&page=${pageno}&pageSize=15`;

    fetch(url)
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        setNews(data.articles);
      })
      .catch((error) => {
        console.error("Fetch error:", error);
      });


  return (
    <div className="container h-100 w-100" style={{minHeight:"100vh", minWidth:"95vw", margin: "0 auto"}}>
      <h1 className="text-center my-2">News app</h1>
      <div className="row d-flex" style={{justifyContent: "space-evenly"}}>
        {news.map((newsItem) => (
          <Newslist
            key={newsItem.source.id}
            imageURL={newsItem.urlToImage}
            title={newsItem.title}
            desc={newsItem.description}
          />
        ))}
      </div>
      <div className="my-3"style={{maxWidth: "80vw",display: "flex", justifyContent: "space-between"}}>
        <button className="btn btn-secondary" onClick={handleClickOnPrevious}>previous&larr;</button>
        <button className="btn btn-primary" onClick={handleClickOnNext}>next&rarr;</button>
      </div>
    </div>
  );
}
