import React, { useState, useEffect } from "react";
import Newslist from "./newslist";

export default function Main() {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(false);
  const [page, setpage] = useState(1);
  const [totalpages, setTotalPages] = useState(1);

  function handleClickOnPrev() {
    setpage((e) => Math.max(e - 1, 1));
  }

  function handleClickOnNext() {
    setpage((e) => Math.min(e + 1, totalpages));
  }

  useEffect(() => {
    const url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=246319e5bf884ea5814b2a0b9f940a09&pagesize=15&page=${page}`;

    setLoading(true);

    fetch(url)
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        setNews(data.articles);
        setTotalPages(Math.ceil(data.totalResults / 12));
        console.log(data);
      })
      .catch((error) => {
        console.error("Fetch error:", error);
      })
      .finally(() => setLoading(false));
  }, [page, totalpages]); // Empty dependency array to execute only once on component mount

  const loadingPercentage = loading ? 40 : 0; 
  return (
    <div className="container h-100 w-100" style={{ minHeight: "100vh", minWidth: "95vw", margin: "0 auto" }}>
      <h1 className="text-center my-2">News app</h1>
      {loading ? (
         <div style={{ position: "absolute",zIndex:"999", top: "100" ,height: "100px", backgroundColor: "#ddd" }}>
         <span
           style={{
            transition: "5s ease-out",
             position: "sticky",
             top: "0",
             left: "0",
             height: "100%",
             width: `${loadingPercentage}%`,
             backgroundColor: "red",
             animation: "progress 5s ease-in forwards",
           }}
         ></span>
       </div>
      ) : (
        <>
          <div className="row d-flex" style={{ justifyContent: "space-evenly" }}>
            {news.map((newsItem) => (
              <Newslist
                key={newsItem.source.url}
                imageURL={newsItem.urlToImage}
                title={newsItem.title}
                desc={newsItem.description}
              />
            ))}
          </div>
          <div className="my-2" style={{ maxWidth: "80vw", display: "flex", justifyContent: "space-between", margin: "0 auto" }}>
            <button className="btn btn-secondary" disabled={page <= 1} onClick={handleClickOnPrev}>
              &larr; Previous
            </button>
            <button className="btn btn-primary" disabled={page >= totalpages - 1} onClick={handleClickOnNext}>
              Next &rarr;
            </button>
          </div>
        </>
      )}
    </div>
  );
}
