import React, { useState, useEffect } from "react";
import Newslist from "./newslist";

export default function Main() {
  const [news, setNews] = useState([]);
  const [pageno, setPageno] = useState(1);

  useEffect(() => {
    const fetchData = async () => {
      const url = 'https://newsnow.p.rapidapi.com/newsv2';
      const options = {
        method: 'POST',
        headers: {
          'content-type': 'application/json',
          'X-RapidAPI-Key': '43e4c5b983msh43ef0f0b889a10cp12c248jsnf756ee60e589',
          'X-RapidAPI-Host': 'newsnow.p.rapidapi.com'
        },
        body: JSON.stringify({
          query: 'AI',
          page: 1,
          time_bounded: true,
          from_date: '01/02/2021',
          to_date: '05/06/2021',
          location: '',
          category: '',
          source: ''
        })
      };

      try {
        const response = await fetch(url, options);
        const result = await response.json(); // Assuming the response is JSON
        console.log(result);
        setNews(result.articles); // Assuming there's an 'articles' property in the result
      } catch (error) {
        console.error("Fetch error:", error);
      }
    };

    fetchData(); // Call the fetchData function
  }, [pageno]); // Run this effect whenever pageno changes

  return (
    <div
      className="container h-100 w-100"
      style={{ minHeight: "100vh", minWidth: "95vw", margin: "0 auto" }}
    >
      {/* ... (rest of your JSX) */}
    </div>
  );
}
