import React, { useState, useEffect } from "react";
import Newslist from "./newslist";

export default function Main() {
  const [news, setNews] = useState([]);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  function handleClickOnPrev() {
    setPage((prevPage) => Math.max(prevPage - 1, 1));
  }

  function handleClickOnNext() {
    setPage((prevPage) => Math.min(prevPage + 1, totalPages));
  }

  useEffect(() => {
    const url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=246319e5bf884ea5814b2a0b9f940a09&pagesize=12&page=${page}`;

    fetch(url)
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        setNews(data.articles);
        setTotalPages(Math.ceil(data.totalResults / 12));
        console.log(data);
      })
      .catch((error) => {
        console.error("Fetch error:", error);
      });
  }, [page]); // Empty dependency array to execute only once on component mount

  return (
    <div className="container h-100 w-100" style={{ minHeight: "100vh", minWidth: "95vw", margin: "0 auto" }}>
      <h1 className="text-center my-2">News app</h1>
      <div className="row d-flex" style={{ justifyContent: "space-evenly" }}>
        {news.map((newsItem) => (
          <Newslist
            key={newsItem.source.url}
            imageURL={newsItem.urlToImage}
            title={newsItem.title}
            desc={newsItem.description}
          />
        ))}
      </div>
      <div className="my-2" style={{ maxWidth: "80vw", display: "flex", justifyContent: "space-between", margin: "0 auto" }}>
        <button className="btn btn-primary" disabled={page <= 1} onClick={handleClickOnPrev}>
          previous&larr;
        </button>
        <button className="btn btn-primary" disabled={page === totalPages} onClick={handleClickOnNext}>
          next&rarr;
        </button>
      </div>
    </div>
  );
}
