import React, { useState, useEffect } from "react";
import Newslist from "./newslist";

export default function Main() {
  // const [news, setNews] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const url = 'https://newsnow.p.rapidapi.com/newsv1';
      const options = {
        method: 'POST',
        headers: {
          'content-type': 'application/json',
          'X-RapidAPI-Key': 'a724b4b8d4msh68ec04f1e3374f1p1bd17fjsnb8a777204f84',
          'X-RapidAPI-Host': 'newsnow.p.rapidapi.com'
        },
        body: JSON.stringify({
          query: 'AI',
          page: 1,
          time_bounded: true,
          from_date: '01/02/2021',
          to_date: '05/06/2021',
          location: '',
          category: '',
          source: ''
        })
      };

      try {
        const response = await fetch(url, options);
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const result = await response.json(); // Assuming the response is JSON
        console.log(result);
      } catch (error) {
        console.error("Fetch error:", error);
      }
    };

    fetchData();
  }, []); // Empty dependency array to run the effect once when the component mounts

  return (
    <div className="container h-100 w-100" style={{ minHeight: "100vh", minWidth: "95vw", margin: "0 auto" }}>
      <h1 className="text-center my-2">News app</h1>
      <div className="row d-flex" style={{ justifyContent: "space-evenly" }}>
        {/* {news.map((newsItem) => (
          <Newslist
            key={newsItem.source.id}
            imageURL={newsItem.urlToImage}
            title={newsItem.title}
            desc={newsItem.description}
          />
        ))} */}
      </div>
      <div className="my-3" style={{ maxWidth: "80vw", display: "flex", justifyContent: "space-between" }}>
        <button className="btn btn-secondary">previous&larr;</button>
        <button className="btn btn-primary">next&rarr;</button>
      </div>
    </div>
  );
}
