import React, { useState, useEffect } from "react";
import Newslist from "./newslist";

export default function Main() {
  const [news, setNews] = useState([]);
  const [pageno, setPageno] = useState(1);

  

  return (
    <div
      className="container h-100 w-100"
      style={{ minHeight: "100vh", minWidth: "95vw", margin: "0 auto" }}
    >
      <h1 className="text-center my-2">News app</h1>
      <div className="row d-flex" style={{ justifyContent: "space-evenly" }}>
        {news.map((newsItem) => (
          <Newslist
            key={newsItem.source.id}
            imageURL={newsItem.urlToImage}
            title={newsItem.title}
            desc={newsItem.description}
          />
        ))}
      </div>
      <div
        className="my-3"
        style={{
          maxWidth: "80vw",
          display: "flex",
          justifyContent: "space-between",
        }}
      >
        <button className="btn btn-secondary">previous&larr;</button>
        <button className="btn btn-primary">next&rarr;</button>
      </div>
    </div>
  );
}
