import React, { useState, useEffect } from "react";
import Newslist from "./newslist";

export default function Main() {
  const [news, setNews] = useState([]);

  useEffect(() => {
    const url = "https://newsapi.org/v2/everything?q=tesla&from=2023-10-19&sortBy=publishedAt&apiKey=42d437807ba945d185b73f42f77bae87&pagesize=20";

    fetch(url)
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        setNews(data.articles);
      })
      .catch((error) => {
        console.error("Fetch error:", error);
      });
  }, []); // Empty dependency array to execute only once on component mount

  return (
    <>
    <div className="container h-100 w-100" style={{minHeight:"100vh", minWidth:"95vw", margin: "0 auto"}}>
      <h1 className="text-center my-2">News app</h1>
      <div className="row d-flex" style={{justifyContent: "space-evenly"}}>
        {news.map((newsItem) => (
          <Newslist
            key={newsItem.source.id}
            imageURL={newsItem.urlToImage}
            title={newsItem.title}
            desc={newsItem.description}
          />
        ))}
      </div>
    </div>
    <div className="container">
      <div className="row">
        <div className="col">
          button.btn.btn-peim
        </div>
        <div className="col"></div>
      </div>
    </div>
   </>
  );
}
