import React, { useState, useEffect } from "react";
import Newslist from "./newslist";

export default function Main() {
  const [news, setNews] = useState([]);

  useEffect(() => {
    const url = "https://newsapi.org/v2/top-headlines?country=us&category=business&apiKey=42d437807ba945d185b73f42f77bae87";

    fetch(url)
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        setNews(data.articles);
      })
      .catch((error) => {
        console.error("Fetch error:", error);
      });
  }, []); // Empty dependency array to execute only once on component mount

  return (
    <div className="container h-100 w-100" style={{minHeight:"100vh", minWidth:"100vw"}}>
      <div className="row">
        
        {news.map((newsItem) => (
          <Newslist
            key={newsItem.source.id}
            imageURL={newsItem.urlToImage}
            title={newsItem.title}
            desc={newsItem.description}
          />
        ))}
      </div>
    </div>
  );
}
